import React from 'react';
import { Socket, Channel } from 'phoenix';
import { TextField, Button } from '@material-ui/core'
import { Formik, Form } from 'formik'

type Props = {
}

type State = {
  inLobby: string
  messages: Message[]
  channel?: Channel
  formState: Message
}

type Message = {
  name: string
  message: string
}

class Chat extends React.Component<Props,State> {
  constructor(props: Props) {
    super(props);
    this.state = {
      messages: [],
      inLobby: "nope",
      formState: {name: "", message: ""}
    }
  }

  onShout = (payload: Message) => {
    this.setState((state: State) => ({
      messages: state.messages.concat(payload)
    }))
  }

  onSubmit = (values: Message) => {
    console.log(values)
  }

  componentDidMount() {
    let socket = new Socket('ws://localhost:4000/socket')
    socket.connect()
    let channel = socket.channel("chat_room:lobby")
    channel.join()
      .receive("ok", _ => {
        console.log("in lobby")
        this.setState({inLobby: "yep", channel: channel})
      })
    channel.on('shout', this.onShout)
  }

  render() {
    return (
      <div>
        <div>
            In lobby: {this.state.inLobby}
        </div>
        <div>
          <Formik onSubmit={this.onSubmit} initialValues={{name:'',message:''}}>
            <Form>
              <div>
                <TextField id='name' label='name' name="name"/>
              </div><div>
                <TextField id='message' label='Message' name="message"/>
              </div><div>
                <Button variant="contained" color="primary" type="submit"> Send </Button>
              </div>
            </Form>
          </Formik>
        </div>
        <div>
          Messages: {this.state.messages.map(m => <p>{JSON.stringify(m, null, 2)} </p>) }
        </div>
    </div>
  )}
}

export default Chat;
